contracts/MedicineRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

interface IMediToken {
    function mint(address to, uint256 amount) external;
}

/**
 * @title MedicineRegistry V2
 * @dev Enhanced registry with complete supply chain tracking
 */
contract MedicineRegistry is AccessControl, ReentrancyGuard {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    // Roles
    bytes32 public constant MANUFACTURER_ROLE = keccak256("MANUFACTURER_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    
    // Supply chain roles
    enum ParticipantRole {
        NONE,
        TRANSPORTER,
        SUPPLIER,
        DISTRIBUTOR,
        WHOLESALER,
        RETAILER
    }
    
    struct SupplyChainParticipant {
        address participantAddress;
        ParticipantRole role;
        bool hasVerified;
        uint256 verificationTimestamp;
        string location; // Optional: GPS or location name
        string notes;    // Optional: condition notes
    }
    
    struct MedicineBatch {
        address manufacturer;
        string batchId;
        string drugName;
        string ingredients;
        uint256 expiryDate;
        uint256 registeredAt;
        
        // Supply chain tracking
        SupplyChainParticipant[] supplyChain;
        mapping(address => bool) isParticipant;
        uint256 requiredVerifications;
        uint256 completedVerifications;
        
        // Customer reward tracking
        bool rewardClaimed;
        address rewardClaimant;
        uint256 rewardClaimedAt;
        
        // Batch status
        bool exists;
        bool isFullyVerified;
    }
    
    // Storage
    mapping(string => MedicineBatch) public batches;
    mapping(address => string[]) public manufacturerBatches;
    mapping(address => string[]) public participantBatches;
    
    // Token contract
    IMediToken public mediToken;
    
    // Constants
    uint256 public constant CUSTOMER_REWARD = 1 ether; // 1 MEDI token
    uint256 public constant MAX_PARTICIPANTS = 20; // Prevent gas attacks
    
    // Events
    event BatchRegistered(
        string indexed batchId,
        address indexed manufacturer,
        uint256 participantCount,
        uint256 timestamp
    );
    
    event ParticipantAdded(
        string indexed batchId,
        address indexed participant,
        ParticipantRole role
    );
    
    event BatchVerified(
        string indexed batchId,
        address indexed verifier,
        ParticipantRole role,
        uint256 verificationNumber,
        uint256 totalRequired
    );
    
    event BatchFullyVerified(
        string indexed batchId,
        uint256 timestamp
    );
    
    event RewardClaimed(
        string indexed batchId,
        address indexed customer,
        uint256 amount,
        uint256 timestamp
    );
    
    event VerificationFailed(
        string indexed batchId,
        address indexed attemptedVerifier,
        string reason
    );
    
    constructor(address _mediToken) {
        mediToken = IMediToken(_mediToken);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MANUFACTURER_ROLE, msg.sender);
    }
    
    /**
     * @dev Register a new medicine batch with supply chain participants
     */
    function registerBatchWithSupplyChain(
        string memory _batchId,
        string memory _drugName,
        string memory _ingredients,
        uint256 _expiryDate,
        address[] memory _participants,
        ParticipantRole[] memory _roles,
        string[] memory _locations
    ) external onlyRole(MANUFACTURER_ROLE) {
        require(!batches[_batchId].exists, "Batch already exists");
        require(_participants.length == _roles.length, "Mismatched arrays");
        require(_participants.length == _locations.length, "Mismatched locations");
        require(_participants.length <= MAX_PARTICIPANTS, "Too many participants");
        require(_participants.length > 0, "At least one participant required");
        require(_expiryDate > block.timestamp, "Expiry date must be in future");
        
        // Initialize batch
        MedicineBatch storage batch = batches[_batchId];
        batch.manufacturer = msg.sender;
        batch.batchId = _batchId;
        batch.drugName = _drugName;
        batch.ingredients = _ingredients;
        batch.expiryDate = _expiryDate;
        batch.registeredAt = block.timestamp;
        batch.exists = true;
        batch.requiredVerifications = _participants.length;
        batch.completedVerifications = 0;
        batch.isFullyVerified = false;
        batch.rewardClaimed = false;
        
        // Add supply chain participants
        for (uint256 i = 0; i < _participants.length; i++) {
            require(_participants[i] != address(0), "Invalid participant address");
            require(_roles[i] != ParticipantRole.NONE, "Invalid role");
            require(!batch.isParticipant[_participants[i]], "Duplicate participant");
            
            batch.supplyChain.push(SupplyChainParticipant({
                participantAddress: _participants[i],
                role: _roles[i],
                hasVerified: false,
                verificationTimestamp: 0,
                location: _locations[i],
                notes: ""
            }));
            
            batch.isParticipant[_participants[i]] = true;
            participantBatches[_participants[i]].push(_batchId);
            
            emit ParticipantAdded(_batchId, _participants[i], _roles[i]);
        }
        
        manufacturerBatches[msg.sender].push(_batchId);
        
        emit BatchRegistered(
            _batchId,
            msg.sender,
            _participants.length,
            block.timestamp
        );
    }
    
    /**
     * @dev Supply chain participant verifies batch receipt
     */
    function verifyBatchAsParticipant(
        string memory _batchId,
        string memory _notes,
        bytes memory _signature
    ) external nonReentrant {
        MedicineBatch storage batch = batches[_batchId];
        require(batch.exists, "Batch does not exist");
        require(!batch.rewardClaimed, "Batch already delivered to customer");
        require(batch.isParticipant[msg.sender], "Not authorized participant");
        
        // Verify signature
        bytes32 messageHash = keccak256(
            abi.encodePacked(_batchId, msg.sender, block.timestamp)
        );
        bytes32 ethSignedMessageHash = messageHash.toEthSignedMessageHash();
        address signer = ethSignedMessageHash.recover(_signature);
        require(signer == msg.sender, "Invalid signature");
        
        // Find participant and update verification
        bool verified = false;
        ParticipantRole verifierRole = ParticipantRole.NONE;
        
        for (uint256 i = 0; i < batch.supplyChain.length; i++) {
            if (batch.supplyChain[i].participantAddress == msg.sender) {
                require(!batch.supplyChain[i].hasVerified, "Already verified");
                
                batch.supplyChain[i].hasVerified = true;
                batch.supplyChain[i].verificationTimestamp = block.timestamp;
                batch.supplyChain[i].notes = _notes;
                
                batch.completedVerifications++;
                verifierRole = batch.supplyChain[i].role;
                verified = true;
                break;
            }
        }
        
        require(verified, "Verification failed");
        
        emit BatchVerified(
            _batchId,
            msg.sender,
            verifierRole,
            batch.completedVerifications,
            batch.requiredVerifications
        );
        
        // Check if all verifications complete
        if (batch.completedVerifications == batch.requiredVerifications) {
            batch.isFullyVerified = true;
            emit BatchFullyVerified(_batchId, block.timestamp);
        }
    }
    
    /**
     * @dev Customer claims reward after all verifications complete
     */
    function claimCustomerReward(string memory _batchId) external nonReentrant {
        MedicineBatch storage batch = batches[_batchId];
        
        require(batch.exists, "Batch does not exist");
        require(batch.isFullyVerified, "Supply chain verification incomplete");
        require(!batch.rewardClaimed, "Reward already claimed");
        require(batch.expiryDate > block.timestamp, "Medicine expired");
        
        // Mark reward as claimed
        batch.rewardClaimed = true;
        batch.rewardClaimant = msg.sender;
        batch.rewardClaimedAt = block.timestamp;
        
        // Mint reward tokens to customer
        mediToken.mint(msg.sender, CUSTOMER_REWARD);
        
        emit RewardClaimed(
            _batchId,
            msg.sender,
            CUSTOMER_REWARD,
            block.timestamp
        );
    }
    
    /**
     * @dev Check batch verification status
     */
    function getBatchStatus(string memory _batchId) 
        external 
        view 
        returns (
            bool exists,
            bool isFullyVerified,
            bool rewardClaimed,
            uint256 completedVerifications,
            uint256 requiredVerifications,
            address rewardClaimant
        ) 
    {
        MedicineBatch storage batch = batches[_batchId];
        return (
            batch.exists,
            batch.isFullyVerified,
            batch.rewardClaimed,
            batch.completedVerifications,
            batch.requiredVerifications,
            batch.rewardClaimant
        );
    }
    
    /**
     * @dev Get supply chain participants for a batch
     */
    function getSupplyChainParticipants(string memory _batchId) 
        external 
        view 
        returns (
            address[] memory participants,
            ParticipantRole[] memory roles,
            bool[] memory hasVerified,
            uint256[] memory timestamps
        ) 
    {
        MedicineBatch storage batch = batches[_batchId];
        require(batch.exists, "Batch does not exist");
        
        uint256 length = batch.supplyChain.length;
        participants = new address[](length);
        roles = new ParticipantRole[](length);
        hasVerified = new bool[](length);
        timestamps = new uint256[](length);
        
        for (uint256 i = 0; i < length; i++) {
            participants[i] = batch.supplyChain[i].participantAddress;
            roles[i] = batch.supplyChain[i].role;
            hasVerified[i] = batch.supplyChain[i].hasVerified;
            timestamps[i] = batch.supplyChain[i].verificationTimestamp;
        }
        
        return (participants, roles, hasVerified, timestamps);
    }
    
    /**
     * @dev Get verification history with details
     */
    function getVerificationHistory(string memory _batchId)
        external
        view
        returns (
            address[] memory verifiers,
            uint256[] memory timestamps,
            string[] memory locations,
            string[] memory notes
        )
    {
        MedicineBatch storage batch = batches[_batchId];
        require(batch.exists, "Batch does not exist");
        
        uint256 verifiedCount = 0;
        for (uint256 i = 0; i < batch.supplyChain.length; i++) {
            if (batch.supplyChain[i].hasVerified) {
                verifiedCount++;
            }
        }
        
        verifiers = new address[](verifiedCount);
        timestamps = new uint256[](verifiedCount);
        locations = new string[](verifiedCount);
        notes = new string[](verifiedCount);
        
        uint256 index = 0;
        for (uint256 i = 0; i < batch.supplyChain.length; i++) {
            if (batch.supplyChain[i].hasVerified) {
                verifiers[index] = batch.supplyChain[i].participantAddress;
                timestamps[index] = batch.supplyChain[i].verificationTimestamp;
                locations[index] = batch.supplyChain[i].location;
                notes[index] = batch.supplyChain[i].notes;
                index++;
            }
        }
        
        return (verifiers, timestamps, locations, notes);
    }
    
    /**
     * @dev Check if address can verify a batch
     */
    function canVerifyBatch(string memory _batchId, address _participant) 
        external 
        view 
        returns (bool canVerify, bool alreadyVerified) 
    {
        MedicineBatch storage batch = batches[_batchId];
        if (!batch.exists || !batch.isParticipant[_participant]) {
            return (false, false);
        }
        
        for (uint256 i = 0; i < batch.supplyChain.length; i++) {
            if (batch.supplyChain[i].participantAddress == _participant) {
                return (true, batch.supplyChain[i].hasVerified);
            }
        }
        
        return (false, false);
    }
    
    /**
     * @dev Emergency: Allow manufacturer to update participant
     */
    function updateParticipant(
        string memory _batchId,
        address _oldParticipant,
        address _newParticipant
    ) external onlyRole(MANUFACTURER_ROLE) {
        MedicineBatch storage batch = batches[_batchId];
        require(batch.exists, "Batch does not exist");
        require(batch.manufacturer == msg.sender, "Not batch owner");
        require(!batch.rewardClaimed, "Batch already delivered");
        
        for (uint256 i = 0; i < batch.supplyChain.length; i++) {
            if (batch.supplyChain[i].participantAddress == _oldParticipant) {
                require(!batch.supplyChain[i].hasVerified, "Already verified");
                
                batch.supplyChain[i].participantAddress = _newParticipant;
                batch.isParticipant[_oldParticipant] = false;
                batch.isParticipant[_newParticipant] = true;
                
                break;
            }
        }
    }
}

